    @Test
    fun reverse_UIntArray() {
        val arrays = (0..4).map { n -> (1..n).map { it.toUInt() }.toUIntArray() }
        for (array in arrays) {
            val original = array.toList()
            array.reverse()
            val reversed = array.toList()
            assertEquals(original.asReversed(), reversed)
        }
    }

